class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        bool flag = false;
        for (char c : s) {
            if (c == '0') {
                flag = true;
                continue;
            }
            if (c == '1') {
                if (flag) {
                    ans += cnt;
                    flag = false;
                    ++cnt;
                } else {
                    ++cnt;
                }
            }
        }
        if (flag) {
            ans += cnt;
        }
        return ans;
    }
};
