class Solution {
public:
    int maxOperations(string s) {
        vector<int> mx(s.size(), 0);
        int pind = -1;
        int ans = 0;
        for(int i = s.size() - 1; i >= 0; i--){
            if(s[i] == '0'){
                continue;
            }
            if(pind == -1){
                if(i == s.size() - 1) {
                    mx[i] = 0;
                }
                else {
                    mx[i] = 1;
                }
            }
            else {
                mx[i] = mx[pind] + (i + 1 == pind? 0 : 1);
            }
            pind = i;
            ans += mx[i];
        }
        return ans;
    }
};
