class Solution:
    def maxOperations(self, s: str) -> int:
        groups = []
        i = 0
        while i < len(s):
            ct = 0
            while i < len(s) and s[i] == '1':
                i += 1
                ct += 1
            if ct != 0:
                groups.append(ct)
            i += 1
        if s[-1] != '0':
            groups.pop()
        res = 0
        pref = 0
        for i in groups:
            pref += i
            res += pref
        return res
