class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int t=-1;
        int n=s.size();
        int ans=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0' && t==-1){
                count++;
                t=0;
            }
            if(s[i]=='1'){
                ans+=count;
                t=-1;
            }
        }
        return ans;
    }
};
