class Solution {
public:
    int maxOperations(string s) {
        int t=0;
        int ans=0;
        bool flag=false;
        for(char ch:s)
        {
            if(ch=='1')
            {
                flag=true;
                t++;
            }
            else
            {
                if(flag)
                {
                    ans+=t;
                    flag=false;
                }
            }
        }
        return ans;
    }
};
