class Solution {
public:
    int maxOperations(string s) {
        s += '1';

        int res = 0;
        int prev = 0;
        int z = 0;

        int n = s.size();

        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                z = 1;
                continue;
            }

            if (z) res += prev;
            prev++;
            z = 0;
        }

        return res;
    }
};
