class Solution {
public:
    int maxOperations(string s) 
    {
        int len=s.size();
        vector<pair<int,int>> v;
        for(int i=0;i<len;i++)
        {
            char c=s[i];
            int st=i;
            while(i+1<len&&s[i+1]==c) i++;
            v.push_back({c-'0',i-st+1});
        }


        int ans=0;
        int siz=v.size();
        vector<int> out;
        for(int i=0;i<siz;i++)
        {
            if(v[i].first==1)
            {
                if(i!=siz-1) out.push_back(v[i].second);
            }
        }
        siz=out.size();
        for(int i=1;i<siz;i++) out[i]+=out[i-1];
        for(int i=0;i<siz;i++) ans+=out[i];
        return ans;

    }
};
