class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');
        int ans=0;
        int cnt=0;
        bool flag=false;
        for(char c:s){
            if(c=='1'){
                if(flag){
                    flag=false;
                    ans+=cnt;
                }
                cnt++;
            }
            else flag=true;
        }

        return ans;
    }
};
