class Solution {
    public int maxOperations(String s) {
		final int n = s.length();
		int i = 0;
		int j = 0;
		int ans = 0;
		while (i<n) {
			final char c = s.charAt(i);
			if (c=='1') {
				++i;
				continue;
			}
			int i0 = i;
			while (++i < n && s.charAt(i)=='0') ;
			ans += i0 - j;
			j += i-i0;
		}
		return ans;
    }
}
