class Solution {
public:
    int maxOperations(string s) {
        int now = 0, ans = 0;
        for(int i = s.size() - 1, j; i >= 0; i = j - 1){
            j = i;
            if(s[i] == '1'){
                ans += now;
                continue;
            }
            while(j > 0 && s[j - 1] == '0') --j;
            now += 1;
        }
        return ans;
    }
};
