class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();

        int ans = 0;
        int cnt = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '1') {
                ans += cnt;
            }
            if (s[i] == '0') {
                if (i + 1 == n || s[i + 1] == '1') {
                    cnt++;
                }
            }
        }
        return ans;
    }
};
