class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0,num=0,flag=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='0'){
                if(flag==1)
                   ans+=num;
                flag=0;
            }
            else{
                flag=1;
                num++;
            }
        }
        return ans;
    }
};
