class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        n = len(s)
        prev = 0
        first = True
        for i in range(n):
            if s[i] == "1":
                prev += 1
                first = True
            else:
                if first:
                    res += prev
                    first = False

        return res
