class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int f[100005];
        memset(f, 0, sizeof(f));
        f[s.size()]=0;
        int pre = 0;
        for(int i = s.size()-1; i >= 0; i--) {
            if(s[i] == '1' && i + 1 < s.size() &&  s[i+1] == '0') {
                f[i] = pre + 1;
                pre = f[i];

            } else {
                if(s[i] =='1') {
                    f[i] = f[i+1];
                }
            }


        }
        int ans =0 ;
        for(int i = 0; i <s.size(); i++) {
            ans += f[i];
        }
        return ans;
    }
};
