public class Solution {
    public int MaxOperations(string s) {
        int ans = 0;

        int idx = 0;
        int cnt1 = 0;
        while (idx < s.Length){
            if (s[idx] == '0'){
                idx++;
                while (idx < s.Length && s[idx] == '0'){
                    idx++;
                }

                ans += cnt1;
            }else{
                cnt1 ++;
                idx++;
            }
        }

        return ans;
    }
}
