class Solution:
    def maxOperations(self, s: str) -> int:
        ss = []
        for char in s:
            if char == "1":
                ss.append(1)
            else:
                if not ss:
                    continue
                elif ss[-1] == 0:
                    continue
                else:
                    ss.append(0)

        prev_one = 0
        result = 0 
        for num in ss:
            if num == 1:
                prev_one += 1
            else:
                result += prev_one
        return result
