class Solution {
public:
    int maxOperations(string s) {
        s+='1';
        long long res=0;
        long long num=s[0]=='1';
        for(int i=1;i<s.size();i++){
            if(s[i]=='0')continue;
            if(s[i-1]=='0')res+=num;
            num++;
        }
        return res;
    }
};
