class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, res = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') cnt++;
            else {
                res += cnt;
                while(i + 1 < s.size() && s[i + 1] == '0') i++;
            }
        }
        return res;
    }
};
