class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int flag=0;
        int n=s.size();
        int cnt=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]-'0')
            {
                cnt++;
                flag=1;
            }
            else
            {
                if(flag) ans+=cnt;
                flag=0;
            }
        }
        return ans;
    }
};
