class Solution {
public:
    int maxOperations(string s) {
        reverse(s.begin(), s.end());
        int ans = 0;
        int lastmove = 0;
        char last = '1';
        for(auto &c : s){
            int move = 0;
            if (c == '1'){
                move = lastmove;
                if (last != '1')
                    lastmove ++;
                ans += lastmove;
            }
            last = c;
        }
        return ans;
    }
};
