class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int one = 0;
        int zero = 0;
        for(int i = 0 ; i < s.length() ; i++){
            if(s.charAt(i)=='0'){
                zero++;
            }else{
                if(zero>0)
                ans += one;
                zero = 0;
                one++;
            }
        }
        if(zero>0){
            ans += one;
        }
        return ans;
    }
}
