class Solution {
public:
    int maxOperations(string s) {
        int length = s.length();
        int current = -1;
        int number_of_ones_present;
        number_of_ones_present=0; int answ = 0;
        for(int i=0;i<length;i++)
        {
            if(s[i]=='0' &&  current!=-1)
            {
                answ+=number_of_ones_present;
                current=-1;
            }
            else if(s[i]=='1' && current==-1)
            {
                current = 1;
                number_of_ones_present++;
            }
            else if(s[i]=='1')
            {
                number_of_ones_present++;
            }
        }
        return answ;
    }
};
