class Solution {
public:
    int maxOperations(string s) {
        reverse(s.begin(),s.end());

        int ans =0;
        int cnt =0;
        for(int i=1;i<s.length();i++)
        {
            if(s[i]=='1'&&s[i]!=s[i-1])
            {
                cnt++;
            }
            if(s[i]=='1')
                ans+=cnt;
        }
        return ans;
    }
};
