class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        cur = 0
        z = False
        for n in s:
            if n == "1":
                if z:
                    res += cur
                cur += 1
                z = False
            else:
                z = True

        if z:
            res += cur
        return res
