class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int o=0,w=0,ans=0;
        if(s[0]=='1')
        o=1;
        for(int i=1;i<n;i++)
        {
            if(s[i]=='1')
            o++;
            else
            {
                if(s[i-1]=='1')
                ans+= o;
            }
        }
        return ans;

    }
};
