class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();

        int cnt=0,ans=0,flag=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                if(flag)ans+=cnt;
                flag=false;
            }
            else{
               cnt++;
               flag=true;
            } 
        }
        return ans;
    }
};
