class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        std::vector<std::array<int, 3>> v;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                int j = i;
                while (j < n && s[j] == '1') {
                    j++;
                }
                v.push_back({i, j - 1, j - i});
                i = j - 1;
            }
        }
        v.push_back({n, n, 0});
        // for (int i = 0; i < v.size(); i++) {
        //     std::cout << v[i].first << ' ' << v[i].second << '\n';
        // }
        int ans = 0;
        for (int i = 1; i < v.size(); i++) {
            ans += ((v[i][0] - v[i - 1][1] - 1) != 0) * v[i - 1][2];
            v[i][2] += v[i - 1][2];
        }
        return ans;
    }
};
