class Solution {
    public int maxOperations(String s) {

        int n = s.length();
        int ans = 0;

        int curr = s.charAt(0) == '1' ? 1 : 0;
        for(int i = 1; i < n; i++) {
            if(s.charAt(i) == '0' && s.charAt(i - 1) == '1') {
                ans += curr;
            }
            else if(s.charAt(i) == '1') curr++;
        }

        return ans;

    }
}
