class Solution {

    public int maxOperations(String s) {
        char[] str = s.toCharArray();
        int n = str.length;
        int cnt = 0;
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (i - 1 >= 0 && str[i] == '1' && str[i - 1] == '0') {
                ans += cnt;
            }
            cnt += str[i] - '0';
        }
        return ans + (str[n - 1] == '0' ? cnt : 0);
    }
}
