class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0, t = 0;
        for(int i = 0; i < n; i ++ ) {
            if(s[i] == '1') {
                int r = i;
                while(r < n && s[r] == s[i]) r ++;
                if(r == n) break;
                t += r - i;
                ans += t;
                i = r - 1;
            }
        }
        return ans;
    }
};
