class Solution {
public:
    int maxOperations(string s) {
        const int n = s.size();
        int res = 0;
        for (int i = n - 1, len = 0, cnt = 0; i >= 0; --i) {
            if (s[i] == '0') {
                ++len;
            } else {
                if (len > 0) {
                    //cout << cnt << " : " << len << endl;
                    ++cnt;
                }
                len = 0;

                res += cnt;
            }
        }
        return res;
    }
};
