class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, ans = 0, n = s.length();
        int last = 1;
        for (int i = n - 1; i >= 0; --i) {
            if (s[i] == '1') {
                last = 1;
                ans += cnt;
            } else {
                if (last) ++cnt;
                last = 0;
            }
        }
        return ans;

    }
};
