class Solution:
    def maxOperations(self, s: str) -> int:

        res = 0

        ans = 0
        n = len(s)
        idx = 0
        while idx<n:
            i = s[idx]
            if i=='0':
                res+=ans
                while idx+1<n and s[idx+1]=='0':
                    idx+=1
            else:
                ans+=1
            idx+=1

        return res
