class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int count = 0;
        for (int i = 0; i < s.size() - 1; i++) {
            char c = s[i];
            if (c == '1') {
                count++;
                if (s[i + 1] == '0') {
                    res += count;
                }
            }
        }
        return res;
    }
};
