class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        result = 0
        length = len(s)
        move_cishu = [0] * length
        last_one = -1
        for i in range(length-1,-1,-1):
            if s[i] == "0":
                move_cishu[i] = 0
            else:  #这个数字为1
                if i == length - 1:
                    move_cishu == 0
                else:
                    if s[i+1] == "1":
                        move_cishu[i] = move_cishu[i+1]
                    else: #中间的1,后面是0
                        if last_one == -1:
                            move_cishu[i] = 1
                        else:
                            move_cishu[i] = move_cishu[last_one] + 1
                last_one = i
        #print(move_cishu)
        for i in range(length):
            result += move_cishu[i]
        return result
