class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0
        cnt = s.count('1')
        temp = 0
        zero_found = False
        for i in range(n - 1, -1, -1):
            if(s[i] == '0'):
                zero_found = True
            else:
                if(zero_found):
                    ans += cnt - temp

                temp += 1
                zero_found = False

        return ans
