class Solution:
    def maxOperations(self, s: str) -> int:
        z_block_cnt = 0
        res = 0
        prev_z = False
        for l in reversed(s):
            if l == "0":
                if prev_z: continue
                z_block_cnt += 1
                prev_z = True
            else:
                res += z_block_cnt
                prev_z = False
        return res
