class Solution {
public:
    int maxOperations(string s) {
        // move the left most one all the way to the right
        vector<int> vec;
        int cnt = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '1') {
                cnt++;
            } else {
                if (cnt > 0) {
                    vec.push_back(cnt);
                }
                cnt = 0;
            }
        }

        int pre = 0, ans = 0;
        for (int num: vec) {
            pre += num;
            ans += pre;
        }
        return ans;
    }
};
