class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt1 = 0, n = s.size();
        for (int i = 0, j; i < n; ) {
            if (s[i] == '1') {
                cnt1++;
                i++;
            } else {
                for (j = i; j < n && s[j] == '0'; j++);
                i = j;
                ans += cnt1;
            }
        }
        return ans;
    }
};
