class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0;
        for (int i = n - 1, cur = s.back() == '0' ? 1 : 0, ss = 0; i >= 0; i--) {
            int x = s[i] - '0';
            if (x == 0) {
                ans += cur * ss;
                if (ss != 0) {
                    cur++;
                }
                ss = 0;
            } else {
                ss++;
            }
            if (i == 0) {
                ans += cur * ss;
            }
        }
        return ans;
    }
};
