class Solution {
public:
    int maxOperations(string s) {
        int res = 0, n = 0;
        for (int i = 0; i < s.size(); ) {
            while (i < s.size() && s[i] == '0') i++;
            while (i < s.size() && s[i] == '1') {
                i++;
                n++;
            }
            if (i == s.size()) break;
            res += n;
        }
        return res;
    }
};
