class Solution {
public:
    int maxOperations(string s) {
        s.push_back('.');
        int cc=0,tot_one=0,ans=0;
        char prev=s[0];
        for(auto t:s) {
            if(prev==t) {
                cc++;
            } else {
                if(prev=='1'&&t!='.') {
                    tot_one+=cc;
                    ans+=tot_one;
                }
                prev=t;
                cc=1;
            }
        }
        return ans;
    }
};

