class Solution {
public:
    int maxOperations(string s) {
        int ones=0,res=0;
        bool frst=false;
        for ( int i = 0 ; i < int(s.size()) ; ++i ) {
            if ( s[i]=='1' ) {
                ++ones;
                frst=true;
            } else {
                if(frst){
                    frst=false;
                    res += ones;
                }
            }
        }
        return res;
    }
};
