class Solution {
public:
    int maxOperations(string s) {
        int zz = 0;
        int n = s.size();
        int ans = 0;
        int cnt = 0;
        int mxCnt = 0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                int j = i;
                while(j<n && s[j]=='1')
                {
                    j++;
                }
                if(j<n)
                {
                    cnt+=(j-i);
                    ans+= cnt;
                }
                --j;
                i=j;
            }
        }
        return ans;
    }
};
