class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        vector<int> dp(n,0);
        int next=-1;
        for(int i=n-2;i>=0;i--){

            if(s[i]=='1'){

                if(s[i+1]=='0'){
                    dp[i]++;
                }
                if(next != -1){
                    dp[i]+=dp[next];    
                }
                next=i;
            }
        }
        int ctr=0;
        for(int i=0;i<n;i++){
            ctr+=dp[i];
        }
        return ctr;
    }
};
