class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();


        int i = 0;
        int cur = 0;
        while (i < n) {
            if (s[i] == '0') {
                i++;
                continue;
            }

            while (i < n && s[i] == '1') {
                cur++;
                i++;
            }
            if (i == n) continue;
            ans += cur;
        }
        return ans;
    }
};
