class Solution {
public:
    long long maxOperations(string s) {
        int n = (int) s.length();

        long long res = 0;
        int cnt1 = 0;
        for (int i = 0; i < n; ++i) {
            if (s[i] == '1') {
                ++cnt1;
            } else {
                res += cnt1;
                while (i + 1 < n && s[i + 1] == '0') {
                    ++i;
                }
            }
        }

        return res;
    }
};
