class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int cur = 0;
        boolean zero = false;

        for(int i = 0; i < s.length(); i++){
            if(s.charAt(i) == '0'){
                zero = true;
            }
            else if(zero){
                zero = false;
                ans += cur;
                cur++;
            }
            else{
                cur++;
            }
        }
        if(zero) ans += cur;
        return ans;

    }
}
