class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        for (int i = 0; i + 1 < s.size(); i++) {
            cnt += s[i] == '1';
            if (s[i] == '1' and s[i + 1] == '0') {
                ans += cnt;
            }
        }
        return ans;
    }
};
