class Solution {
public:
    int maxOperations(string s1) {
        while (s1.back() == 1)   s1.pop_back();
        string s = "";
        for (int i = 0; i < s1.size(); i ++) {
            s += s1[i];
            if (s1[i] == '0' && (i > 0 && s1[i - 1] == '0'))  s.pop_back();
        }


        int n = s.length();
        int res = 0;
        int f = n, c = 0, cnt = 0;

        // 1001101
        for (int i = n - 1; i >= 0; i -- ) {
            if (s[i] == '0')    f = i, c = 0, cnt ++;
            else {
                c ++;
                if (f == n) continue;
                if (c > 1)  res += cnt;
                else    res += cnt;
            }
        }

        return res;
    }
};
